import React, { useState } from 'react';

export default function App() {
  const [file, setFile] = useState(null);
  const [query, setQuery] = useState("");
  const [answer, setAnswer] = useState([]);
  
  const uploadFile = async (e) => {
    e.preventDefault();
    if (!file) {
      alert("Please select a file first!");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("http://127.0.0.1:8000/api/upload", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`HTTP ${response.status} - ${errorText}`);
      }

      const data = await response.json();
      console.log("Upload success:", data);
      alert(`File uploaded successfully: ${data.filename}`);
    } catch (error) {
      console.error("Upload failed:", error);
      alert("Upload failed. Check backend logs.");
    }
  };

  const ask = async (e) => {
    e.preventDefault();
    if (!query) return;

    try {
      const res = await fetch("http://127.0.0.1:8000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ query }),
      });

      const j = await res.json();

      // If backend returns multiple sources, display array of {text, filename}
      if (j.sources) {
        setAnswer(j.sources);
      } else {
        setAnswer([{ text: j.answer || JSON.stringify(j), filename: "Unknown" }]);
      }

    } catch (err) {
      console.error("Chat failed:", err);
      setAnswer([{ text: "Error fetching answer.", filename: "" }]);
    }
  };

  return (
    <div style={{ padding: 20, fontFamily: 'Arial' }}>
      <h2>IDP â€” Upload & Chat</h2>

      <form onSubmit={uploadFile}>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} />
        <button type="submit">Upload</button>
      </form>

      <hr />

      <form onSubmit={ask}>
        <input
          style={{ width: 400 }}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Ask about uploaded documents..."
        />
        <button type="submit">Ask</button>
      </form>

      <div style={{ marginTop: 20 }}>
        <strong>Answer:</strong>
        <div style={{ whiteSpace: 'pre-wrap', marginTop: 8 }}>
          {answer.length === 0 && <p>No answer yet.</p>}
          {answer.map((item, idx) => (
            <div key={idx} style={{ marginBottom: 12 }}>
              <div style={{ fontStyle: "italic", color: "gray" }}>
                Source: {item.filename}
              </div>
              <div>{item.text}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
